// incializa contador a 0 si no existe ningun mulo registrado
var elements = context.get('modulos')||[];

for (var i=0;i<elements.length;i++) {
    if (elements[i].mac == msg.payload.mac){
        elements[i] =msg.payload
        msg.payload = elements
        return msg;
    }
}
elements.push(msg.payload);
msg.payload = elements
context.set('modulos',elements)
return msg;

// traduce la lista de modulos para visualizarse en el dashboard
var modulos = msg.payload
msg.payload = []

for (let i=0;i<modulos.length;i++) {
    lista = {
        title: "<font color=\"orange\"><b>"+modulos[i].name+"</b></font>",
        description : modulos[i].mac +" - " + modulos[i].ip,
        icon_name: "fa-link"
    }
    msg.payload.push(lista);
}

return msg;

